import React, { useState } from 'react';


const SignUp = () => {
    const [name, setName] = useState('');
    const [lastname, setLastname] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    let json = {
        name: name,
        lastname: lastname,
        email: email,
        password: password
    }

    const updateEmailField = (e) => {
        //console.log(e.target.name);
        let value = e.target.value;
        e.target.name==='name' ? setName(value) : e.target.name==='lastname' ? setLastname(value) : e.target.name==='email' ? setEmail(value) : setPassword(value);

    }

    const handleSubmit = (e) => {
        console.log(e.target);
    }

    return (
        <>
{/*             <div id='jsonObject' style={{display:'none'}} >
                {json}
            </div> */}
            <form>
                <label>
                    <h1>Name:</h1>
                    <input onChange={updateEmailField} type="text" name="name"/>
                    <h1>Lastname:</h1>
                    <input onChange={updateEmailField} type="text" name="lastname"/>
                    <h1>Email:</h1>
                    <input onChange={updateEmailField} type="email" name="email"/>
                    <h1>Password:</h1>
                    <input onChange={updateEmailField} type="password" name="password"/>
                </label>
                <input onSubmit={handleSubmit} type="submit" value="Submit" />
            </form>
        </>
    );
}

